mapper.update:
UPDATE device
device_name=#{device.deviceName}, device_type_id=#{device.deviceTypeId}, device_status_id=#{device.deviceStatusId}, device_status=#{device.deviceStatus}, device_purchase_date=#{device.devicePurchaseDate}, device_purchase_price=#{device.devicePurchasePrice}, device_manufacture_date=#{device.deviceManufactureDate}, device_service_life=#{device.deviceServiceLife}, device_keeper_id=#{device.deviceKeeperId}, note=#{device.note}
WHERE device_id = #{device.deviceId}
